import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';

// Import the default marker icon
import 'leaflet/dist/images/marker-icon.png';
import 'leaflet/dist/images/marker-shadow.png';

const MapComponent = ({ location }) => {
    const [coordinates, setCoordinates] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    const fetchCoordinates = async () => {
        try {
            const response = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${location}`);
            if (response.data && response.data.length > 0) {
                var { lat, lon } = response.data[0];
                setCoordinates([parseFloat(lat), parseFloat(lon)]);
            }
        } catch (error) {
            console.error('Error fetching coordinates:', error);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchCoordinates();
    }, [location]);

    if (isLoading) {
        return <p>Map is loading...</p>;
    }

    if (!coordinates) {
        return <p>Unable to fetch coordinates</p>;
    }

    return (
        <MapContainer center={coordinates} zoom={7} style={{ height: '400px', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {coordinates && (
                <Marker position={coordinates}>
                    {location && (
                        <Popup>Location: {location}</Popup>
                    )}
                </Marker>
            )}
        </MapContainer>
    );
};

export default MapComponent;
