import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Redirect, Switch } from 'react-router-dom';
import './App.css';
import Login from './components/Login';
import MainContent from './MainContent';
import PredictionPage from './components/PredictionPage';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Callback function to set the user as logged in
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  // Callback function to set the user as logged out
  const handleLogout = () => {
    const confirmLogout = window.confirm('Are you sure you want to logout?');
    if (confirmLogout) {
      setIsLoggedIn(false);
      window.location.href = '/login';
    }
  };

  return (
    <Router>
      <div className="app">
        {isLoggedIn && (
          <div className="logout-button" onClick={handleLogout}>
            Logout
          </div>
        )}

        <div className="content">
          <Switch>
            {/* Redirect to login page if not logged in */}
            <Route path="/login" exact>
              {isLoggedIn ? <Redirect to="/" /> : <Login onLogin={handleLogin} />}
            </Route>

            {/* Main content route */}
            <Route path="/" exact>
              {isLoggedIn ? <MainContent /> : <Redirect to="/login" />}
            </Route>

            {/* Prediction page route */}
            <Route path="/prediction" exact>
              {isLoggedIn ? <PredictionPage /> : <Redirect to="/login" />}
            </Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
