import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';

const MapComponent = ({ location }) => {
    const [coordinates, setCoordinates] = useState(null);
    const [loading, setLoading] = useState(true);
  
    useEffect(() => {
      // Use a geocoding service to get coordinates based on the location
      const getCoordinates = async () => {
        try {
          const response = await axios.get(`https://nominatim.openstreetmap.org/search?format=json&q=${location}`);
          if (response.data && response.data.length > 0) {
            const { lat, lon } = response.data[0];
            setCoordinates([parseFloat(lat), parseFloat(lon)]);
          }
          console.log('Response from geocoding service:', response);
        } catch (error) {
          console.error('Error fetching coordinates:', error);
        } finally {
          setLoading(false);  // <-- Set loading to false after fetching coordinates
        }
      };
  
      getCoordinates();
    }, [location]);
    return (
        <MapContainer center={coordinates} zoom={10} style={{ height: '400px', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <Marker position={coordinates}>
                <Popup>Location: {location}</Popup>
            </Marker>
        </MapContainer>
    );
};

export default MapComponent;
