import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

function Login({ onLogin }) {
    const [name, setName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [emailError, setEmailError] = useState('');

    const handleNameChange = (event) => {
        setName(event.target.value);
    };

    const handleLastNameChange = (event) => {
        setLastName(event.target.value);
    };

    const handleEmailChange = (event) => {
        setEmail(event.target.value);
        setEmailError('');
    };

    // Validate email address
    const isEmailValid = (email) => {
        const emailRegex = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i;
        return emailRegex.test(email);
    };

    const handleLogin = async () => {
        if (name && lastName) {
            if (isEmailValid(email)) {
                const loginData = {
                    name,
                    lastName,
                    email,
                };

                try {
                    const fileName = 'login.json';

                    const existingData = localStorage.getItem('loginData');
                    if (existingData) {
                        const newData = JSON.parse(existingData);
                        newData.push(loginData);
                        localStorage.setItem('loginData', JSON.stringify(newData));
                    } else {
                        localStorage.setItem('loginData', JSON.stringify([loginData]));
                    }

                    onLogin();
                } catch (error) {
                    console.error('Error saving login data:', error);
                    alert('Error saving login data.');
                }
            } else {
                setEmailError('Please enter a valid email address.');
            }
        } else {
            alert('Please fill in all fields.');
        }
    };

    return (
        <div className="login-container">
            <div className="login-image-bg"></div>
            <div className="login-form">
                <h2 className="login-heading">Login</h2>
                <div className="divider d-flex align-items-center my-4"></div>
                <div className="mb-4">
                    <input className="form-control" type="text" placeholder="First Name" onChange={handleNameChange} />
                </div>
                <div className="mb-4">
                    <input className="form-control" type="text" placeholder="Last Name" onChange={handleLastNameChange} />
                </div>
                <div className="mb-4">
                    <input className="form-control" type="email" placeholder="Email Address" onChange={handleEmailChange} />
                    <span style={{ color: 'red' }}>{emailError}</span>
                </div>
                <div className="text-center text-md-start mt-3">
                    <button className="login-button" onClick={handleLogin}>
                        Login
                    </button>
                </div>
            </div>
        </div>
    );
}

export default Login;
