import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import ReactSwitch from 'react-switch';
import Login from './Login';

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Set the initial state to false

  // Check local storage for login status when the component mounts
  useEffect(() => {
    const storedLoginStatus = localStorage.getItem('isLoggedIn');
    if (storedLoginStatus === 'true') {
      setIsLoggedIn(true);
    }
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem('isLoggedIn', 'true');
  };

  const handleLogout = () => {
    const confirmLogout = window.confirm("Are you sure you want to logout?");
    if (confirmLogout) {
      setIsLoggedIn(false);
      localStorage.setItem('isLoggedIn', 'false');
    }
  };

  return (
    <Router>
      <div className={`App ${darkMode ? 'dark' : ''}`}>
        <div className="title-bar">
          <h1>Genome Sequencing</h1>
          <label className="dark-mode-label">
            <span>Dark Mode</span>
            <ReactSwitch
              onChange={toggleDarkMode}
              checked={darkMode}
              onColor="#007acc"
              offColor="#ccc"
              checkedIcon={false}
              uncheckedIcon={false}
            />
          </label>
        </div>
        <nav className="navigation">
          <ul>
            <li>Home</li>
            <li>About</li>
            <li>Contact</li>
          </ul>
        </nav>
        <div className="file-upload">
          <label className="upload-button">
            <input
              type="file"
              accept=".txt, .fasta, .fastq, .csv"
              style={{ display: 'none' }}
            />
            <span>Select File</span>
          </label>
        </div>
      </div>

      {isLoggedIn && (
        <div className="logout-button" onClick={handleLogout}>
          Logout
        </div>
      )}

      <div className="content">
        <div className="App container py-3">
          {isLoggedIn ? (
            <div>
              <h1>Welcome to the Genome Sequencing App</h1>
              <p>
                This is a simple example of a React component.
              </p>
              <p>
                If you see this page, the login was successful.
              </p>
            </div>
          ) : (
            <Login onLogin={handleLogin} />
          )}
        </div>
      </div>
    </Router>
  );
}

export default App;
