import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Login from './Login';
import MainContent from './MainContent'; // Make sure to import your MainContent component

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('isLoggedIn') === 'true');

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem('isLoggedIn', 'true');
  };

  const handleLogout = () => {
    const confirmLogout = window.confirm("Are you sure you want to logout?");
    if (confirmLogout) {
      setIsLoggedIn(false);
      localStorage.setItem('isLoggedIn', 'false');
    }
  };

  return (
    <Router>
      <div className="app">
        {isLoggedIn && (
          <div className="logout-button" onClick={handleLogout}>
            Logout
          </div>
        )}
        <Switch>
          <Route path="/login">
            {isLoggedIn ? <Redirect to="/app" /> : <Login onLogin={handleLogin} />}
          </Route>
          <Route path="/app">
            <div className={`App ${darkMode ? 'dark' : ''}`}>
              <div className="title-bar">
                <h1>Genome Sequencing</h1>
                <label className="dark-mode-label">
                  <span>Dark Mode</span>
                  <Switch
                    onChange={toggleDarkMode}
                    checked={darkMode}
                    onColor="#007acc"
                    offColor="#ccc"
                    checkedIcon={false}
                    uncheckedIcon={false}
                  />
                </label>
              </div>
              <nav className="navigation">
                <ul>
                  <li>Home</li>
                  <li>About</li>
                  <li>Contact</li>
                </ul>
              </nav>
              <div className="file-upload">
                <label className="upload-button">
                  <input
                    type="file"
                    accept=".txt, .fasta, .fastq"
                    onChange={handleFileChange}
                    style={{ display: 'none' }}
                  />
                  <span>Select File</span>
                </label>
                {selectedFile && <p>Selected File: {selectedFile.name}</p>}
              </div>
            </div>
          </Route>
          <Redirect from="/" to={isLoggedIn ? "/app" : "/login"} />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
