import React, { useState } from 'react';
import './CompareSequences.css'; // Import the updated comparesequences.css file

function CompareSequences() {
    const [selectedFile1, setSelectedFile1] = useState(null);
    const [selectedFile2, setSelectedFile2] = useState(null);
    const [fileContent1, setFileContent1] = useState('');
    const [fileContent2, setFileContent2] = useState('');

    const handleFileChange1 = (e) => {
        const file = e.target.files[0];
        setSelectedFile1(file);

        if (file) {
            const reader = new FileReader();
            reader.onload = (event) => {
                const contents = event.target.result;
                const firstFewLines = contents.split('\n').slice(0, 3).join('\n');
                setFileContent1(firstFewLines);
            };
            reader.readAsText(file);
        }
    };

    const handleFileChange2 = (e) => {
        const file = e.target.files[0];
        setSelectedFile2(file);

        if (file) {
            const reader = new FileReader();
            reader.onload = (event) => {
                const contents = event.target.result;
                const firstFewLines = contents.split('\n').slice(0, 3).join('\n');
                setFileContent2(firstFewLines);
            };
            reader.readAsText(file);
        }
    };

    const handleCompareSequences = () => {
        // Logic to compare sequences based on selectedFile1 and selectedFile2
        // For example:
        if (fileContent1 && fileContent2) {
            // Perform comparison logic here
            console.log('Files compared successfully.');
        } else {
            console.error('Please upload both files to compare.');
        }
    };

    return (
        <div className="App">
            <div className="compare-genome-section">
                <div className="compare-genome-section-title">
                    Compare Sequences
                    <p className="compare-genome-section-intro">
                        Upload two sequence files to compare them.
                    </p>
                </div>

                <div className="compare-sections">
                    <div className="compare-file-content-box">
                        <h3>File 1</h3>
                        <input
                            type="file"
                            accept=".txt, .fasta, .fastq"
                            onChange={handleFileChange1}
                        />
                        {fileContent1 && (
                            <p>{fileContent1}</p>
                        )}
                    </div>

                    <div className="compare-file-content-box">
                        <h3>File 2</h3>
                        <input
                            type="file"
                            accept=".txt, .fasta, .fastq"
                            onChange={handleFileChange2}
                        />
                        {fileContent2 && (
                            <p>{fileContent2}</p>
                        )}
                    </div>
                </div>

                <div className="compare-button-wrapper">
                    <button className="compare-button" onClick={handleCompareSequences}>
                        Compare Sequences
                    </button>
                </div>
            </div>
        </div>
    );
}

export default CompareSequences;
