import React, { useState } from 'react';
import './App.css';
import { Switch as ReactSwitch } from 'react-switch';
import Login from './Login';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';


function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('isLoggedIn') === 'true');

  // Check local storage for login status when the component mounts
  useEffect(() => {
    const storedLoginStatus = localStorage.getItem('isLoggedIn');
    if (storedLoginStatus === 'true') {
      setIsLoggedIn(true);
    }
  }, []);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem('isLoggedIn', 'true');
  };

  return (
    <Router>
      <div className={`App ${darkMode ? 'dark' : ''}`}>
        <div className="title-bar">
          <h1>Genome Sequencing</h1>
          <label className="dark-mode-label">
            <span>Dark Mode</span>
            <ReactSwitch
              onChange={toggleDarkMode}
              checked={darkMode}
              onColor="#007acc"
              offColor="#ccc"
              checkedIcon={false}
              uncheckedIcon={false}
            />
          </label>
        </div>
        <nav className="navigation">
          <ul>
            <li>Home</li>
            <li>About</li>
            <li>Contact</li>
          </ul>
        </nav>
        <div className="file-upload">
          <label className="upload-button">
            <input
              type="file"
              accept=".txt, .fasta, .fastq"
              onChange={handleFileChange}
              style={{ display: 'none' }}
            />
            <span>Select File</span>
          </label>
          {selectedFile && <p>Selected File: {selectedFile.name}</p>}
        </div>
      </div>
      <RouterSwitch>
        <Route path="/login" render={() => (isLoggedIn ? <Redirect to="/app" /> : <Login />)} />
        <Route path="/app" render={() => (isLoggedIn ? <App /> : <Redirect to="/login" />)} />
        <Redirect from="/" to="/login" />
      </RouterSwitch>
    </Router>
  );
}

export default App;
