import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import ReactSwitch from 'react-switch';
import Login from './Login';
import Navigation from './Navigation'; // Import your Navigation component
import AudioPlayerWithTextForm from './AudioPlayerWithTextForm'; // Import your AudioPlayerWithTextForm component

function App() {
  const [darkMode, setDarkMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('isLoggedIn') === 'true');

  useEffect(() => {
    const storedLoginStatus = localStorage.getItem('isLoggedIn');
    if (storedLoginStatus === 'true') {
      setIsLoggedIn(true);
    }
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem('isLoggedIn', 'true');
  };

  return (
    <Router>
      <div className={`App ${darkMode ? 'dark' : ''}`}>
        <div className="title-bar">
          <h1>Genome Sequencing</h1>
          <label className="dark-mode-label">
            <span>Dark Mode</span>
            <ReactSwitch
              onChange={toggleDarkMode}
              checked={darkMode}
              onColor="#007acc"
              offColor="#ccc"
              checkedIcon={false}
              uncheckedIcon={false}
            />
          </label>
        </div>
        <nav className="navigation">
          <ul>
            <li>Home</li>
            <li>About</li>
            <li>Contact</li>
          </ul>
        </nav>
        <div className="file-upload">
          <label className="upload-button">
            <input
              type="file"
              accept=".txt, .fasta, .fastq"
              style={{ display: 'none' }}
            />
            <span>Select File</span>
          </label>
        </div>
      </div>

      {isLoggedIn && (
        <div>
          <div className="logout-button" onClick={handleLogout}>
            Logout
          </div>
          <div className="download-button" onClick={handleDownloadLoginData}>
            Download Login Data
          </div>
          <Navigation
            numberOfChunks={numberOfTranscripts}
            handleChunkSelect={handleChunkSelect}
            chunkColors={chunkColors}
            setChunkColors={setChunkColors}
          />
        </div>
      )}

      <div className="content">
        <Navbar title="Speech Transcription Verification App" mode={darkMode ? 'dark' : 'light'} toggleMode={toggleDarkMode} />
        <Alert alert={alert} />

        <div className="App container py-3">
          {isLoggedIn ? (
            <AudioPlayerWithTextForm
              selectedChunk={selectedChunk}
              transcriptNumber={transcriptNumber}
              setTranscriptNumber={setTranscriptNumber}
              text={text}
              audioSrc={audioSrc}
              isAudio={isAudio}
              setText={setText}
              setAudioSrc={setAudioSrc}
              setIsAudio={setIsAudio}
              setChunkColors={setChunkColors}
            />
          ) : (
            <Login onLogin={handleLogin} />
          )}
        </div>
      </div>
    </Router>
  );
}

export default App;
