import React, { useState, useEffect } from 'react';
import './App.css';

import AudioPlayerWithTextForm from './components/AudioPlayerWithTextForm';
import Alert from './components/Alert';
import Login from './components/Login/Login';
import { BrowserRouter as Router, Switch } from 'react-router-dom';
import Navigation from './components/Navigation';
import axios from 'axios';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [darkMode, setDarkMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('isLoggedIn') === 'true');


  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem('isLoggedIn', 'true'); // Set login state in localStorage
  };

  const handleLogout = () => {
    const confirmLogout = window.confirm("Are you sure you want to logout?");
    if (confirmLogout) {
      setIsLoggedIn(false);
      localStorage.setItem('isLoggedIn', 'false'); // Set login state in localStorage to false
    }
  };

  return (
    <Router>
      <div className="app">
        {isLoggedIn && (
          <div className="logout-button" onClick={handleLogout}>
            Logout
          </div>
        )}

        <div className={`App ${darkMode ? 'dark' : ''}`}>
          <div className="title-bar">
            <h1>Genome Sequencing</h1>
            <label className="dark-mode-label">
              <span>Dark Mode</span>
              <Switch
                onChange={toggleDarkMode}
                checked={darkMode}
                onColor="#007acc"
                offColor="#ccc"
                checkedIcon={false}
                uncheckedIcon={false}
              />
            </label>
          </div>
          <nav className="navigation">
            <ul>
              <li>Home</li>
              <li>About</li>
              <li>Contact</li>
            </ul>
          </nav>
          <div className="file-upload">
            {isLoggedIn ? (
              <Switch>
                <label className="upload-button">
                  <input
                    type="file"
                    accept=".txt, .fasta, .fastq"
                    onChange={handleFileChange}
                    style={{ display: 'none' }} // Hide the default input element
                  />
                  <span>Select File</span>
                </label>
                {selectedFile && <p>Selected File: {selectedFile.name}</p>}
              </Switch>
            ) : (
              <Login onLogin={handleLogin} />
            )}
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
